
public class For_loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// looping mean to continue based on given condition
		// purpose of this program is to print all the value from 0 to 29
		
		for (int i=0;  // string value i=o
				i<50;  // condition
				i+=1)
		{
			System.out.println(i);
		}
		
		
		
		
		
		
	}

}
